import React, { useState } from "react";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";
import "./ExpensesFilter.css";
import ExpensesList from "./ExpensesList";
import ExpensesChart from "./ExpensesChart";
const Expenses= (props) => {
const [filteredYear, setFilteredYear] = useState('2020');



const filterChangeHandler = (SelectedYear) => {
setFilteredYear(SelectedYear);
};

const filteredExpenses = props.items.filter(expense =>{
  return expense.date.getFullYear().toString() === filteredYear; //funkcija napravljena za filtriranje, dakle ako zelimo prikazati odredjenu godinu, poredimo je sa filteredYear i zadrzavamo u pomocnom nizu ako je true
});

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onChangeFilter={filterChangeHandler}
        />
      <ExpensesList items={filteredExpenses} />   

      </Card>
    </div>
  );
};

export default Expenses;
